from typing import List

class Solution:
    def longestOnes(self, nums: List[int], k: int) -> int:
        maxim = 0
        i = 0
        count = 0
        l = 0
        while i < len(nums):
            if nums[i] == 0:
                count += 1
            i += 1
            while count > k:
                if nums[l] == 0:
                    count -= 1
                l += 1
            maxim = max(maxim, i - l)
        return maxim


