from typing import List

class Solution:
    def longestOnes(self, nums: List[int], k: int) -> int:
        # if k == 0:
        #     return nums
        res = self._get_max(nums, k, 0, 0)
        return res

    def _get_max(self, nums, k, start, maxi):
        i = start
        j = 0
        nnums = [0 for _ in nums]
        while i < len(nums):
            if nums[i] == 0 and j < k:
                nnums[i] = 1
                j += 1
            else:
                nnums[i] = nums[i]
            i += 1
        # res = max([len(x) for x in ''.join([str(n) for n in nnums]).split('0')])
        res = 5
        if maxi > res:
            res = maxi
        if start < len(nums):
            res = self._get_max(nums, k, start + 1, res)
        return res

